JIRA.ViewIssue=(function(){function setFocusConfiguration(){if(parseUri(window.location.href).anchor!=="summary"){var triggerConfig=new JIRA.setFocus.FocusConfiguration();triggerConfig.excludeParentSelector="#"+FORM_ID+",.dont-default-focus";JIRA.setFocus.pushConfiguration(triggerConfig)}else{AJS.$("#summary").focus()}}function listenForEvents(){var subtaskTrigger;JIRA.bind("QuickCreateSubtask.sessionComplete",function(e,issues){JIRA.Issue.getSubtaskModule().addClass("updating");JIRA.Issue.refreshSubtasks().done(function(){subtaskTrigger=document.getElementById("stqc_show");if(subtaskTrigger){subtaskTrigger.onclick=null}JIRA.Issue.highlightSubtasks(issues);JIRA.Issue.getSubtaskModule().removeClass("updating")})});JIRA.bind("QuickEdit.sessionComplete",function(){JIRA.Issue.reload()})}var FORM_ID="stqcform";var subtasks={domReady:function(){setFocusConfiguration()}};var stalker={init:function(){new JIRA.OffsetAnchors("#stalker.js-stalker, .stalker-placeholder")}};return{init:function(){stalker.init()},domReady:function(){subtasks.domReady();listenForEvents()}}})();JIRA.ViewIssue.init();AJS.$(JIRA.ViewIssue.domReady);AJS.namespace("jira.app.viewissue",null,JIRA.ViewIssue);jQuery(function(){AJS.moveInProgress=false;AJS.$(document).bind("moveToStarted",function(){AJS.moveInProgress=true}).bind("moveToFinished",function(){AJS.moveInProgress=false})});